package com.xworkz.ControlStatements;

public class BlockPopulation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char block='D';
		switch(block) {
			case 'A':
					System.out.println("A block population is 30000");
					break;
			case 'B':
				System.out.println("B block population is 30000");
				break;
			case 'C':
				System.out.println("C block population is 30000");
				break;
			case 'D':
				System.out.println("D block population is 30000");
				break;
			default :
				System.out.println("Invalid statement");
				
		}
	}

}
